import React from 'react'
import './TeamTile.scss'
import { PieChart } from 'react-minimal-pie-chart'

export const TeamTile = ({ teamName, totalMatches, totalWins }) => {
  return (
    <div className="TeamTile">
      <h1>{teamName}</h1>
      <h2>Total Matches : {totalMatches}</h2>
      <h2>Total Wins : {totalWins}</h2>
      <div className="peichart">
        <PieChart
          data={[
            {
              title: 'Losses',
              value: totalMatches - totalWins,
              color: '#a34d5d',
            },
            { title: 'Wins', value: totalWins, color: '#4da375' },
          ]}
        ></PieChart>
      </div>
    </div>
  )
}
